import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  Box,
  TextField,
  Alert,
  CircularProgress,
} from '@mui/material';
import { Add as AddIcon, Remove as RemoveIcon } from '@mui/icons-material';
import { useCart } from '../contexts/CartContext';
import { flowersAPI } from '../services/api';
import { Flower } from '../types';

const Home: React.FC = () => {
  const [flowers, setFlowers] = useState<Flower[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { addToCart } = useCart();

  useEffect(() => {
    const fetchFlowers = async () => {
      try {
        const data = await flowersAPI.getAll();
        setFlowers(data);
      } catch (err: any) {
        setError('Çiçekler yüklenirken bir hata oluştu.');
      } finally {
        setLoading(false);
      }
    };

    fetchFlowers();
  }, []);

  const handleAddToCart = (flower: Flower) => {
    addToCart(flower, 1);
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="60vh">
        <CircularProgress />
      </Box>
    );
  }

  if (error) {
    return (
      <Container maxWidth="md" sx={{ mt: 4 }}>
        <Alert severity="error">{error}</Alert>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h3" component="h1" gutterBottom align="center" sx={{ mb: 4 }}>
        Çiçek Dünyası
      </Typography>
      <Typography variant="h6" component="h2" gutterBottom align="center" color="text.secondary" sx={{ mb: 4 }}>
        En güzel çiçekler, en uygun fiyatlarla
      </Typography>

      <Grid container spacing={4}>
        {flowers.map((flower) => (
          <Grid xs={12} sm={6} md={4} key={flower.id}>
            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
              <CardMedia
                component="img"
                height="200"
                image={flower.imageUrl || 'https://via.placeholder.com/300x200?text=Çiçek'}
                alt={flower.name}
                sx={{ objectFit: 'cover' }}
              />
              <CardContent sx={{ flexGrow: 1 }}>
                <Typography gutterBottom variant="h5" component="h3">
                  {flower.name}
                </Typography>
                <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                  {flower.description}
                </Typography>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                  <Typography variant="h6" color="primary">
                    ₺{flower.price.toFixed(2)}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Stok: {flower.stockQuantity}
                  </Typography>
                </Box>
                <Button
                  variant="contained"
                  fullWidth
                  onClick={() => handleAddToCart(flower)}
                  disabled={!flower.isAvailable || flower.stockQuantity === 0}
                  startIcon={<AddIcon />}
                >
                  Sepete Ekle
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default Home;
